-- atualizar_pagador_ir
CREATE OR REPLACE PROCEDURE pls_declaracao_ir_pck.atualizar_pagador_ir ( nr_seq_lote_p pls_lote_mens_ir.nr_sequencia%type, nr_seq_pagador_ir_p pls_mens_pagador_ir.nr_sequencia%type) AS $body$
DECLARE


_ora2pg_r RECORD;
nr_indice_w			integer;
tb_nr_seq_pagador_ir_w		pls_util_cta_pck.t_number_table;
tb_ds_email_w			pls_util_cta_pck.t_varchar2_table_255;
tb_vl_mensalidade_w		pls_util_cta_pck.t_number_table;
tb_vl_coparticipacao_w		pls_util_cta_pck.t_number_table;
tb_vl_reembolso_w		pls_util_cta_pck.t_number_table;
tb_vl_desconto_w		pls_util_cta_pck.t_number_table;
tb_vl_devolucao_w		pls_util_cta_pck.t_number_table;
tb_qt_beneficiarios_w		pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_lote_pc pls_lote_mens_ir.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_pagador_ir,
		b.cd_pessoa_fisica,
		b.cd_cgc,
		b.ds_email
	from	pls_mens_pagador_ir	a,
		pls_contrato_pagador	b
	where	b.nr_sequencia	= a.nr_seq_pagador
	and	a.nr_seq_lote	= nr_seq_lote_pc
	and	((a.nr_sequencia = nr_seq_pagador_ir_p) or (coalesce(nr_seq_pagador_ir_p::text, '') = ''));

BEGIN

nr_indice_w	:= 0;
tb_nr_seq_pagador_ir_w.delete;
SELECT * FROM pls_declaracao_ir_pck.update_pagador_ir(tb_nr_seq_pagador_ir_w, tb_ds_email_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w, tb_qt_beneficiarios_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_pagador_ir_w := _ora2pg_r.tb_nr_seq_pagador_ir_p; tb_ds_email_w := _ora2pg_r.tb_ds_email_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p; tb_qt_beneficiarios_w := _ora2pg_r.tb_qt_beneficiarios_p;

for r_c01_w in C01(nr_seq_lote_p) loop
	begin
	tb_nr_seq_pagador_ir_w(nr_indice_w)	:= r_c01_w.nr_seq_pagador_ir;
	
	select	sum(vl_mensalidade),
		sum(vl_coparticipacao),
		sum(vl_reembolso),
		sum(vl_desconto),
		sum(vl_devolucao),
		count(1) qt_beneficiarios
	into STRICT	tb_vl_mensalidade_w(nr_indice_w),
		tb_vl_coparticipacao_w(nr_indice_w),
		tb_vl_reembolso_w(nr_indice_w),
		tb_vl_desconto_w(nr_indice_w),
		tb_vl_devolucao_w(nr_indice_w),
		tb_qt_beneficiarios_w(nr_indice_w)
	from	pls_mens_beneficiario_ir
	where	nr_seq_pagador_ir	= r_c01_w.nr_seq_pagador_ir;
	
	if (r_c01_w.ds_email IS NOT NULL AND r_c01_w.ds_email::text <> '') then
		tb_ds_email_w(nr_indice_w)	:= r_c01_w.ds_email;
	else
		if (r_c01_w.cd_pessoa_fisica IS NOT NULL AND r_c01_w.cd_pessoa_fisica::text <> '') then
			select	max(ds_email)
			into STRICT	tb_ds_email_w(nr_indice_w)
			from	compl_pessoa_fisica
			where	cd_pessoa_fisica	= r_c01_w.cd_pessoa_fisica
			and	ie_tipo_complemento	= 1;
			
			if (coalesce(tb_ds_email_w(nr_indice_w)::text, '') = '') then
				select	max(ds_email)
				into STRICT	tb_ds_email_w(nr_indice_w)
				from	compl_pessoa_fisica
				where	cd_pessoa_fisica	= r_c01_w.cd_pessoa_fisica
				and	ie_tipo_complemento	= 2;
			end if;
		elsif (r_c01_w.cd_cgc IS NOT NULL AND r_c01_w.cd_cgc::text <> '') then
			select	max(ds_email)
			into STRICT	tb_ds_email_w(nr_indice_w)
			from	pessoa_juridica_estab
			where	cd_cgc = r_c01_w.cd_cgc;
		end if;
	end if;
	
	if (nr_indice_w >= pls_util_pck.qt_registro_transacao_w) then
		SELECT * FROM pls_declaracao_ir_pck.update_pagador_ir(tb_nr_seq_pagador_ir_w, tb_ds_email_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w, tb_qt_beneficiarios_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_pagador_ir_w := _ora2pg_r.tb_nr_seq_pagador_ir_p; tb_ds_email_w := _ora2pg_r.tb_ds_email_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p; tb_qt_beneficiarios_w := _ora2pg_r.tb_qt_beneficiarios_p;
		
		nr_indice_w	:= 0;
	else
		nr_indice_w	:= nr_indice_w + 1;
	end if;
	end;
end loop;

SELECT * FROM pls_declaracao_ir_pck.update_pagador_ir(tb_nr_seq_pagador_ir_w, tb_ds_email_w, tb_vl_mensalidade_w, tb_vl_coparticipacao_w, tb_vl_reembolso_w, tb_vl_desconto_w, tb_vl_devolucao_w, tb_qt_beneficiarios_w) INTO STRICT _ora2pg_r;
 tb_nr_seq_pagador_ir_w := _ora2pg_r.tb_nr_seq_pagador_ir_p; tb_ds_email_w := _ora2pg_r.tb_ds_email_p; tb_vl_mensalidade_w := _ora2pg_r.tb_vl_mensalidade_p; tb_vl_coparticipacao_w := _ora2pg_r.tb_vl_coparticipacao_p; tb_vl_reembolso_w := _ora2pg_r.tb_vl_reembolso_p; tb_vl_desconto_w := _ora2pg_r.tb_vl_desconto_p; tb_vl_devolucao_w := _ora2pg_r.tb_vl_devolucao_p; tb_qt_beneficiarios_w := _ora2pg_r.tb_qt_beneficiarios_p;

END;

$body$
