-- deletar_pagadores
CREATE OR REPLACE PROCEDURE pls_declaracao_ir_pck.deletar_pagadores ( nr_seq_lote_p pls_lote_mens_ir.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_indice_w		integer;
tb_nr_seq_pagador_ir_w	pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_lote_pc pls_lote_mens_ir.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_mens_pagador_ir a
	where	a.nr_seq_lote	= nr_seq_lote_pc
	and	not exists (	SELECT	1
				from	pls_mens_beneficiario_ir x
				where	a.nr_sequencia	= x.nr_seq_pagador_ir);

BEGIN

nr_indice_w	:= 0;
tb_nr_seq_pagador_ir_w.delete;
for r_c01_w in C01(nr_seq_lote_p) loop
	begin
	tb_nr_seq_pagador_ir_w(nr_indice_w)	:= r_c01_w.nr_sequencia;
	
	if (nr_indice_w >= pls_util_pck.qt_registro_transacao_w) then
		tb_nr_seq_pagador_ir_w := pls_declaracao_ir_pck.delete_pagador(tb_nr_seq_pagador_ir_w);
		
		nr_indice_w	:= 0;
	else
		nr_indice_w	:= nr_indice_w + 1;
	end if;
	end;
end loop;

tb_nr_seq_pagador_ir_w := pls_declaracao_ir_pck.delete_pagador(tb_nr_seq_pagador_ir_w);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
