-- gerar_lote_ir
CREATE OR REPLACE PROCEDURE pls_declaracao_ir_pck.gerar_lote_ir ( nr_seq_lote_p pls_lote_mens_ir.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_pagadores_w	integer;

BEGIN
update	pls_lote_mens_ir
set	dt_inicio_geracao = clock_timestamp(),
	dt_geracao_lote = clock_timestamp()
where	nr_sequencia = nr_seq_lote_p;
commit;

CALL CALL pls_declaracao_ir_pck.carregar_dados_lote(nr_seq_lote_p);

--Inserir o publico alvo para geracao do IR
CALL pls_declaracao_ir_pck.inserir_pagadores(nr_seq_lote_p,nm_usuario_p);
CALL CALL pls_declaracao_ir_pck.inserir_beneficiarios(nr_seq_lote_p,null,nm_usuario_p);

--Gerar os valores pagos pelo beneficiario no ano base
gerar_valores_ir(nr_seq_lote_p,null,nm_usuario_p);

--Arredondar o valor das baixas com valor de desconto que tenham mais de um beneficiario
CALL gerar_arredondamento(nr_seq_lote_p, null, nm_usuario_p);

--Deletar os beneficiarios e pagadores quando nao existir valores
CALL pls_declaracao_ir_pck.deletar_beneficiarios(nr_seq_lote_p,null,nm_usuario_p);
CALL pls_declaracao_ir_pck.deletar_pagadores(nr_seq_lote_p,nm_usuario_p);

--Atualizar os valores do lote
CALL pls_declaracao_ir_pck.atualizar_benef_ir(nr_seq_lote_p,null);
CALL pls_declaracao_ir_pck.atualizar_pagador_ir(nr_seq_lote_p,null);

select	count(1)
into STRICT	qt_pagadores_w
from	pls_mens_pagador_ir
where	nr_seq_lote	= nr_seq_lote_p;

update	pls_lote_mens_ir
set	dt_fim_geracao	= clock_timestamp(),
	qt_pagadores	= qt_pagadores_w
where	nr_sequencia	= nr_seq_lote_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
