CREATE OR REPLACE PROCEDURE pls_declaracao_ir_pck.inserir_pagadores ( nr_seq_lote_p pls_lote_mens_ir.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


