tb_nr_seq_pagador_w	pls_util_cta_pck.t_number_table;


BEGIN
select	ie_tipo_lote
into STRICT	current_setting('pls_declaracao_ir_pck.ie_tipo_lote_w')::pls_lote_mens_ir.ie_tipo_lote%type
from	pls_lote_mens_ir
where	nr_sequencia = nr_seq_lote_p;

ds_select_w :=	'	select	a.nr_sequencia ' ||
		'	from	pls_contrato_pagador	a ' ||
		'	where	((:ie_tipo_lote_pc = ''A'') or ' ||
