-- procedure responsavel por gerar os dados para o demonstrativo e retornar a versao que deve ser utilizada



CREATE OR REPLACE PROCEDURE pls_demonstrativo_analise_pck.gerar_demonstrativo ( nr_seq_protocolo_p pls_relatorio_analise.nr_sequencia%type, nr_seq_demonstrativo_p w_tiss_demonstra_retorno.nr_sequencia%type, nr_seq_prestador_pag_p pls_relatorio_analise.nr_seq_prestador%type, ie_local_solicitacao_p pls_rel_an_versao.ie_local_solicitacao%type, nr_seq_usuario_web_p pls_regra_demons_analise.nr_seq_usuario%type, ie_itens_manual_p text, nm_usuario_p usuario.nm_usuario%type, nr_seq_versao_rel_p INOUT pls_rel_an_versao.nr_sequencia%type, nr_seq_relatorio_ws_p INOUT pls_relatorio_analise_ws.nr_sequencia%type) AS $body$
DECLARE

								
nr_seq_relatorio_w		pls_relatorio_analise.nr_sequencia%type;
nr_seq_versao_rel_w		pls_rel_an_versao.nr_sequencia%type;
nr_seq_protocolo_w		pls_relatorio_analise.nr_sequencia%type;
nr_seq_relatorio_ws_w		pls_relatorio_analise_ws.nr_sequencia%type;
ie_recurso_glosa_w      	w_tiss_demonstra_ret_itens.ie_rec_glosa%type;

BEGIN

PERFORM set_config('pls_demonstrativo_analise_pck.ie_itens_manuais_w', coalesce(ie_itens_manual_p,'T'), false);
nr_seq_protocolo_w	:= nr_seq_protocolo_p;

ie_recurso_glosa_w := 'N';

if (nr_seq_demonstrativo_p IS NOT NULL AND nr_seq_demonstrativo_p::text <> '') then
	select	max(nr_seq_protocolo),
         	coalesce(max(a.ie_rec_glosa),'N')
	into STRICT	nr_seq_protocolo_w,
       		ie_recurso_glosa_w
	from	w_tiss_demonstra_ret_itens a
	where	nr_seq_demonstra_ret	= nr_seq_demonstrativo_p;
end if;

-- gera ou retorna a sequencia do relatorio

-- alimenta as variaveis globais ie_prest_demonstrativo_gl_w e nr_seq_prestador_rel_gl_w que serao utilizadas durante todo o processo

SELECT * FROM pls_demonstrativo_analise_pck.gerar_relatorio(nr_seq_protocolo_w, nr_seq_prestador_pag_p, nr_seq_usuario_web_p, nm_usuario_p, 'S', nr_seq_relatorio_w, nr_seq_relatorio_ws_w, ie_recurso_glosa_w) INTO STRICT nr_seq_relatorio_w, nr_seq_relatorio_ws_w;

-- gera o registro na tabela pls_rel_an_versao

nr_seq_versao_rel_w := pls_demonstrativo_analise_pck.gerar_versao_relatorio(nr_seq_relatorio_w, nr_seq_relatorio_ws_w, nr_seq_protocolo_p, nr_seq_demonstrativo_p, ie_local_solicitacao_p, nm_usuario_p, nr_seq_versao_rel_w, ie_recurso_glosa_w);

nr_seq_versao_rel_p	:= nr_seq_versao_rel_w;
nr_seq_relatorio_ws_p	:= nr_seq_relatorio_ws_w;

commit;

END;

$body$
