SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_demonstrativo_analise_pck.obter_valores_cta_proc ( nr_seq_conta_proc_p bigint, nr_seq_conta_p bigint, nr_seq_prestador_pgto_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

					
/*ie_tipo_p
A - Valor apresentado
C - Calculado
G - Glosa
*/


vl_procedimento_w		pls_conta_proc.vl_procedimento%type;
qt_prest_pag_w			integer;

BEGIN

if (ie_tipo_p = 'A') then
	select	coalesce(sum(vl_apresentado),0)
	into STRICT	vl_procedimento_w
	from	pls_conta_medica_resumo
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	nr_seq_conta		= nr_seq_conta_p
	and	nr_seq_prestador_pgto	= nr_seq_prestador_pgto_p;
elsif (ie_tipo_p = 'C') then
	
	select	coalesce(sum(vl_calculado),0)
	into STRICT	vl_procedimento_w
	from	pls_conta_medica_resumo
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	nr_seq_conta		= nr_seq_conta_p
	and	nr_seq_prestador_pgto	= nr_seq_prestador_pgto_p;
	
	
elsif (ie_tipo_p = 'G') then
	select	count(1)
	into STRICT 	qt_prest_pag_w	
	from	pls_conta_medica_resumo
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p
	and	nr_seq_conta		= nr_seq_conta_p
	and	nr_seq_prestador_pgto	!= nr_seq_prestador_pgto_p;
	
	if (qt_prest_pag_w		> 0) then
		select	coalesce(sum(vl_glosa),0)
		into STRICT	vl_procedimento_w
		from	pls_conta_medica_resumo
		where	nr_seq_conta_proc	= nr_seq_conta_proc_p
		and	nr_seq_conta		= nr_seq_conta_p
		and	nr_seq_prestador_pgto	= nr_seq_prestador_pgto_p;
	else
		select	max(vl_glosa)
		into STRICT	vl_procedimento_w
		from	pls_conta_proc
		where 	nr_sequencia	= nr_seq_conta_proc_p;
	end if;
end if;
	
return vl_procedimento_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
