SET client_encoding TO 'UTF8';


/*Obter o valor total calculado da conta*/




CREATE OR REPLACE FUNCTION pls_demonstrativo_analise_pck.obter_vl_tot_calculado_cta ( nr_seq_conta_p bigint) RETURNS bigint AS $body$
DECLARE


vl_procedimento_w		pls_conta_proc.vl_procedimento%type;
vl_material_w			pls_conta_mat.vl_material%type;	


BEGIN

select	coalesce(sum(a.vl_procedimento),0)
into STRICT	vl_procedimento_w
from	pls_conta_proc	a
where	a.nr_seq_conta		= nr_seq_conta_p
and	a.ie_status 	not in ('D','M');

select	coalesce(sum(a.vl_material),0)
into STRICT	vl_material_w
from	pls_conta_mat	a
where	a.nr_seq_conta		= nr_seq_conta_p
and 	a.ie_status 	not in ('D','M');

return vl_procedimento_w + vl_material_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
