CREATE OR REPLACE FUNCTION pls_ecarta_integracao_pck.buscar_arquivos ( ds_servico_web_ftps_p text, ds_servidor_p text, nr_porta_p bigint, nm_user_p text, ds_senha_p text, ds_dir_local_p text, ds_dir_remoto_p text, ie_excluir_p text default 'N', nm_arquivos_p out text) AS $body$
DECLARE

	ds_chamada_w	varchar(2083);
	ds_retorno_w	text := null;

BEGIN
	-- Seta variáveis globais
	PERFORM set_config('pls_ecarta_integracao_pck.nm_procedimento_w', 'buscar arquivos', false);

	-- Caso exista, retira "/" no final do endereço do serviço web
	if (substr(ds_servico_web_ftps_p, length(ds_servico_web_ftps_p), 1) = '/') then
		ds_chamada_w := substr(ds_servico_web_ftps_p, 1, length(ds_servico_web_ftps_p));
	else
		ds_chamada_w := ds_servico_web_ftps_p;
	end if;

	-- Prepara parâmetros para a chamada ao serviço web
	ds_chamada_w := ds_chamada_w || '?metodo=downloadFiles';
	ds_chamada_w := ds_chamada_w || chr(38) || 'server='	|| ds_servidor_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'port='	|| nr_porta_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'user='	|| nm_user_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'password='	|| ds_senha_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'localPath='	|| ds_dir_local_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'remotePath='|| ds_dir_remoto_p;

	if (ie_excluir_p = 'S') then
		ds_chamada_w := ds_chamada_w || chr(38) || 'delete=true';
	else
		ds_chamada_w := ds_chamada_w || chr(38) || 'delete=false';
	end if;

	-- Invoca o serviço web
	ds_retorno_w := pls_ecarta_integracao_pck.invocar_servico_web(ds_chamada_w);

	-- Verifica tipo de retorno
	if (position('[' in ds_retorno_w) > 0) then
		-- Retorna erro
		return;
	else
		-- Retorna lista de arquivos baixados
		nm_arquivos_p := ds_retorno_w;

		return;
	end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
