CREATE OR REPLACE PROCEDURE pls_ecarta_integracao_pck.carregar_matriz ( nr_seq_ecarta_matriz_p pls_ecarta_matriz.nr_sequencia%type) AS $body$
DECLARE

	-- Matriz
	c01_w CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_estabelecimento,
		a.cd_identificador,
		a.ds_matriz,
		a.nr_cartao_postagem,
		a.qt_limite_diario,
		a.ie_ar_digital,
		a.ie_arquivo_complementar
	from	pls_ecarta_matriz a
	where	a.nr_sequencia	= nr_seq_ecarta_matriz_p;

BEGIN
	-- Seta variáveis globais
	PERFORM set_config('pls_ecarta_integracao_pck.nm_procedimento_w', 'carregar matriz e-Carta', false);

	-- Carrega parâmetros
	open c01_w;
	fetch c01_w into
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.nr_sequencia,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.cd_estabelecimento,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.cd_identificador,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.ds_matriz,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.nr_cartao_postagem,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.qt_limite_diario,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.ie_ar_digital,
		current_setting('pls_ecarta_integracao_pck.pls_matriz_t')::pls_matriz_r.ie_arquivo_complementar;
	close c01_w;
END;

$body$
