CREATE OR REPLACE FUNCTION pls_ecarta_integracao_pck.enviar_arquivo (ds_servico_web_ftps_p text, ds_servidor_p text, nr_porta_p bigint, nm_user_p text, ds_senha_p text, ds_dir_local_p text, ds_dir_remoto_p text) RETURNS varchar AS $body$
DECLARE

	ds_chamada_w	varchar(2083);

BEGIN
	-- Seta variáveis globais
	PERFORM set_config('pls_ecarta_integracao_pck.nm_procedimento_w', 'enviar arquivo', false);

	-- Caso exista, retira "/" no final do endereço do serviço web
	if (substr(ds_servico_web_ftps_p, length(ds_servico_web_ftps_p), 1) = '/') then
		ds_chamada_w := substr(ds_servico_web_ftps_p, 1, length(ds_servico_web_ftps_p));
	else
		ds_chamada_w := ds_servico_web_ftps_p;
	end if;

	-- Prepara parâmetros para a chamada ao serviço web
	ds_chamada_w := ds_chamada_w || '?metodo=uploadFile';
	ds_chamada_w := ds_chamada_w || chr(38) || 'server='	|| ds_servidor_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'port='	|| nr_porta_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'user='	|| nm_user_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'password='	|| ds_senha_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'localPath='	|| ds_dir_local_p;
	ds_chamada_w := ds_chamada_w || chr(38) || 'remotePath='|| ds_dir_remoto_p;

	-- Retorna a invocação do serviço web
	return pls_ecarta_integracao_pck.invocar_servico_web(ds_chamada_w);
END;

