CREATE OR REPLACE FUNCTION pls_ecarta_integracao_pck.obter_existencia_solicitacao ( nr_seq_ecarta_lote_p pls_ecarta_lote.nr_sequencia%type, nr_seq_ecarta_solicitacao_p pls_ecarta_solicitacao.nr_sequencia%type) RETURNS boolean AS $body$
DECLARE

	qt_registro_w	bigint;

	-- Solicitação não encerrada
	c01_w CURSOR FOR
	SELECT	count(1)
	from	pls_ecarta_solicitacao a
	where	coalesce(a.dt_baixa::text, '') = ''
	and	a.nr_seq_ecarta_lote	= nr_seq_ecarta_lote_p
	and	a.nr_sequencia		= nr_seq_ecarta_solicitacao_p;

BEGIN
	-- Executa consulta
	open c01_w;
	fetch c01_w into qt_registro_w;
	close c01_w;

	-- Se estiver encerrada, retorna que não existe
	if (qt_registro_w > 0) then
		return true;
	else
		return false;
	end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
