CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.aplica_taxas_amb ( vl_tx_custo_oper_p pls_cp_cta_eproc.vl_ch_custo_oper%type, vl_tx_honorarios_p pls_cp_cta_eproc.vl_ch_honorarios%type, vl_moeda_filme_p pls_cp_cta_eproc.cd_moeda_filme%type, tx_ajuste_custo_oper_p pls_cp_cta_eproc.tx_ajuste_custo_oper%type, tx_ajuste_ch_honor_p pls_cp_cta_eproc.tx_ajuste_ch_honor%type, tx_ajuste_filme_p pls_cp_cta_eproc.tx_ajuste_filme%type, vl_custo_operacional_p INOUT bigint, vl_medico_p INOUT bigint, vl_filme_p INOUT bigint) AS $body$
BEGIN

-- aplica taxas/moeda nos valores conforme necessidade de cada campo
vl_custo_operacional_p := vl_custo_operacional_p * vl_tx_custo_oper_p;
vl_medico_p := vl_medico_p * vl_tx_honorarios_p;

-- verifica se é maior que zero, caso não tenha moeda informada o valor neste ponto será zero
if (vl_moeda_filme_p > 0) then

	vl_filme_p := vl_filme_p * vl_moeda_filme_p;
end if;

vl_custo_operacional_p := vl_custo_operacional_p * tx_ajuste_custo_oper_p;
vl_medico_p := vl_medico_p * tx_ajuste_ch_honor_p;
vl_filme_p := vl_filme_p * tx_ajuste_filme_p;

END;

$body$
