CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.aplica_taxas_cbhpm ( qt_uco_cbhpm_p cbhpm_preco.qt_uco%type, vl_tx_custo_oper_p pls_cp_cta_eproc.vl_ch_custo_oper%type, vl_medico_cbhpm_p pls_conta_proc.vl_medico%type, vl_tx_honorarios_p pls_cp_cta_eproc.vl_ch_honorarios%type, qt_filme_cbhpm_p cbhpm_preco.qt_filme%type, vl_filme_p pls_cp_cta_eproc.vl_filme%type, vl_moeda_filme_p pls_cp_cta_eproc.cd_moeda_filme%type, tx_ajuste_filme_p pls_cp_cta_eproc.tx_ajuste_filme%type, tx_ajuste_custo_oper_p pls_cp_cta_eproc.tx_ajuste_custo_oper%type, tx_ajuste_ch_honor_p pls_cp_cta_eproc.tx_ajuste_ch_honor%type, vl_custo_operacional_p INOUT bigint, vl_medico_p INOUT bigint, vl_filme_out_p INOUT bigint, nr_porte_anestesico_p INOUT cbhpm_preco.nr_porte_anest%type, nr_auxiliares_p INOUT cbhpm_preco.nr_auxiliar%type) AS $body$
BEGIN
-- aplica as taxas/moedas necessarias
vl_custo_operacional_p := qt_uco_cbhpm_p * vl_tx_custo_oper_p;
vl_medico_p := vl_medico_cbhpm_p * vl_tx_honorarios_p;
vl_filme_out_p := qt_filme_cbhpm_p * vl_filme_p;

-- se tem moeda informada do filme aplica
if (vl_moeda_filme_p > 0) then
	vl_filme_out_p := vl_filme_out_p * vl_moeda_filme_p;
end if;

-- atribui os valores aos par√¢metros out
vl_custo_operacional_p := coalesce(vl_custo_operacional_p, 0) * tx_ajuste_custo_oper_p;
vl_medico_p := vl_medico_p * tx_ajuste_ch_honor_p;
vl_filme_out_p := vl_filme_out_p * tx_ajuste_filme_p;
nr_porte_anestesico_p := coalesce(nr_porte_anestesico_p,0);
nr_auxiliares_p := coalesce(nr_auxiliares_p,0);


END;

$body$
