CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.atualiza_materiais ( ie_destino_regra_p text, tb_vl_calculado_p INOUT pls_util_cta_pck.t_number_table, tb_vl_calculado_base_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_conta_mat_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_regra_entao_p INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN
-- verifica se tem algo para mandar pro banco
if (tb_nr_seq_conta_mat_p.count > 0) then

	if (ie_destino_regra_p = 'C') then

		forall i in tb_nr_seq_conta_mat_p.first..tb_nr_seq_conta_mat_p.last
			update	pls_conta_mat
			set	vl_mat_copartic = coalesce(tb_vl_calculado_p(i), 0)
			where 	nr_sequencia = tb_nr_seq_conta_mat_p(i);
		commit;

		forall i in tb_nr_seq_conta_mat_p.first..tb_nr_seq_conta_mat_p.last
			update	pls_conta_mat_regra
			set	nr_seq_cp_comb_cop_entao = tb_nr_seq_regra_entao_p(i),
				vl_mat_copartic_regra = coalesce(tb_vl_calculado_base_p(i), 0)
			where	nr_sequencia = tb_nr_seq_conta_mat_p(i);
		commit;
	else		
		-- feito nvl no valor do material, conforme Diogo o correto Ã© apresentar zero
		forall i in tb_nr_seq_conta_mat_p.first..tb_nr_seq_conta_mat_p.last
			update	pls_conta_mat_regra
			set	vl_material_base = coalesce(tb_vl_calculado_p(i), 0),
				nr_seq_cp_comb_entao = tb_nr_seq_regra_entao_p(i)
			where	nr_sequencia = tb_nr_seq_conta_mat_p(i);
		commit;
	end if;
end if;

-- limpa as tabelas
tb_vl_calculado_p.delete;
tb_vl_calculado_base_p.delete;
tb_nr_seq_conta_mat_p.delete;
tb_nr_seq_regra_entao_p.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
