CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.atualiza_participantes ( ie_destino_regra_p text, nr_contador_p INOUT integer, tb_nr_seq_proc_partic_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_regra_entao_p INOUT pls_util_cta_pck.t_number_table, tb_vl_negociado_p INOUT pls_util_cta_pck.t_number_table, tb_vl_medico_p INOUT pls_util_cta_pck.t_number_table, tb_vl_anestesista_p INOUT pls_util_cta_pck.t_number_table, tb_vl_auxiliares_p INOUT pls_util_cta_pck.t_number_table) AS $body$
