				nr_seq_cp_comb_cop_entao = tb_nr_seq_regra_entao_p(i)
			where 	nr_sequencia = tb_nr_seq_proc_partic_p(i);
		commit;
	else
	
		forall i in tb_nr_seq_proc_partic_p.first..tb_nr_seq_proc_partic_p.last
			update	pls_proc_participante
			set	vl_negociado = tb_vl_negociado_p(i),
				vl_medico = tb_vl_medico_p(i),
				vl_medico_base = tb_vl_medico_p(i),
				vl_anestesista = tb_vl_anestesista_p(i),
				vl_anestesista_base = tb_vl_anestesista_p(i),
				vl_auxiliares = tb_vl_auxiliares_p(i),
				vl_auxiliares_base = tb_vl_auxiliares_p(i),
				nr_seq_cp_comb_entao = tb_nr_seq_regra_entao_p(i)
			where 	nr_sequencia = tb_nr_seq_proc_partic_p(i);
		commit;
	end if;
end if;

-- limpa as tabelas
