CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.atualiza_procedimentos ( ie_destino_regra_p text, nr_contador_p INOUT integer, tb_nr_seq_conta_proc_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_regra_entao_p INOUT pls_util_cta_pck.t_number_table, tb_vl_procedimento_p INOUT pls_util_cta_pck.t_number_table, tb_vl_custo_oper_p INOUT pls_util_cta_pck.t_number_table, tb_vl_medico_p INOUT pls_util_cta_pck.t_number_table, tb_vl_anestesista_p INOUT pls_util_cta_pck.t_number_table, tb_vl_auxiliares_p INOUT pls_util_cta_pck.t_number_table, tb_nr_porte_anest_p INOUT pls_util_cta_pck.t_number_table, tb_vl_filme_p INOUT pls_util_cta_pck.t_number_table, tb_nr_auxiliares_p INOUT pls_util_cta_pck.t_number_table, tb_nr_seq_reg_auto_p INOUT pls_util_cta_pck.t_number_table, tb_cd_moeda_auto_p INOUT pls_util_cta_pck.t_varchar2_table_10, tb_vl_medico_tab_p INOUT pls_util_cta_pck.t_number_table, tb_vl_co_tab_p INOUT pls_util_cta_pck.t_number_table, tb_qt_filme_tab_p INOUT pls_util_cta_pck.t_number_table, tb_cd_edicao_amb_p INOUT pls_util_cta_pck.t_number_table, tb_ch_honorario_p INOUT pls_util_cta_pck.t_number_table, tb_ch_custo_oper_p INOUT pls_util_cta_pck.t_number_table, tb_ch_anestesista_p INOUT pls_util_cta_pck.t_number_table, tb_ch_filme_p INOUT pls_util_cta_pck.t_number_table) AS $body$
BEGIN

-- verifica se tem algo para mandar pro banco
if (tb_nr_seq_conta_proc_p.count > 0) then

	if (ie_destino_regra_p = 'C') then

		forall i in tb_nr_seq_conta_proc_p.first..tb_nr_seq_conta_proc_p.last
