				vl_auxiliares_cop = tb_vl_auxiliares_p(i)
			where	nr_sequencia = tb_nr_seq_conta_proc_p(i);
		commit;
	else

		forall i in tb_nr_seq_conta_proc_p.first..tb_nr_seq_conta_proc_p.last
			update	pls_conta_proc
			set	cd_porte_anestesico = tb_nr_porte_anest_p(i),
				nr_auxiliares = tb_nr_auxiliares_p(i),
				nr_seq_preco_autogerado = tb_nr_seq_reg_auto_p(i),
				cd_moeda_autogerado = tb_cd_moeda_auto_p(i),
				vl_medico_tab = tb_vl_medico_tab_p(i),
				vl_custo_operacional_tab = tb_vl_co_tab_p(i),
				qt_filme_tab = tb_qt_filme_tab_p(i),
				vl_ch_honorario = tb_ch_honorario_p(i),
				vl_ch_custo_oper = tb_ch_custo_oper_p(i),
				vl_ch_custo_filme = tb_ch_filme_p(i),
				vl_ch_anestesista = tb_ch_anestesista_p(i)
			where 	nr_sequencia = tb_nr_seq_conta_proc_p(i);
		commit;

		forall i in tb_nr_seq_conta_proc_p.first..tb_nr_seq_conta_proc_p.last
			update	pls_conta_proc_regra
			set	vl_procedimento_base = tb_vl_procedimento_p(i),
				vl_custo_operacional = tb_vl_custo_oper_p(i),
				vl_anestesista = tb_vl_anestesista_p(i),
				vl_medico = tb_vl_medico_p(i),
				vl_materiais = tb_vl_filme_p(i),
				vl_auxiliares = tb_vl_auxiliares_p(i),
				nr_seq_cp_comb_proc_entao = tb_nr_seq_regra_entao_p(i),
				cd_edicao_amb = tb_cd_edicao_amb_p(i)
			where	nr_sequencia = tb_nr_seq_conta_proc_p(i);
		commit;
	end if;
end if;

-- limpa as tabelas
tb_nr_seq_conta_proc_p.delete;
tb_nr_seq_regra_entao_p.delete;
tb_vl_procedimento_p.delete;
tb_vl_custo_oper_p.delete;
tb_vl_medico_p.delete;
tb_vl_anestesista_p.delete;
tb_vl_auxiliares_p.delete;
tb_nr_porte_anest_p.delete;
