CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.define_regra_entao_mat () AS $body$
DECLARE


tb_nr_seq_conta_mat_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_emat_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT 	temp.nr_seq_conta_mat,
		entao.nr_sequencia nr_seq_emat
	from	pls_cp_cta_mat_tmp temp,
		pls_cp_cta_emat entao
	where	entao.nr_seq_cp_combinada = temp.nr_seq_regra_filtro
	and	temp.dt_atendimento between entao.dt_inicio_vigencia_ref
					and entao.dt_fim_vigencia_ref;

BEGIN

open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_conta_mat_w, tb_nr_seq_emat_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_conta_mat_w.count = 0;

	forall i in tb_nr_seq_conta_mat_w.first..tb_nr_seq_conta_mat_w.last
		update	pls_cp_cta_mat_tmp
		set	nr_seq_regra_entao = tb_nr_seq_emat_w(i)
		where 	nr_seq_conta_mat = tb_nr_seq_conta_mat_w(i);
	commit;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
