CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.define_regra_entao_serv () AS $body$
DECLARE


tb_nr_seq_conta_proc_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_eserv_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT 	temp.nr_seq_conta_proc,
		entao.nr_sequencia nr_seq_eproc
	from	pls_cp_cta_proc_tmp temp,
		pls_cp_cta_eserv entao
	where	entao.nr_seq_cp_combinada = temp.nr_seq_cp_comb_filtro
	and	temp.dt_procedimento_referencia between entao.dt_inicio_vigencia_ref
						and entao.dt_fim_vigencia_ref;

BEGIN

open C01;
loop
	fetch C01 bulk collect into tb_nr_seq_conta_proc_w, tb_nr_seq_eserv_w
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_conta_proc_w.count = 0;

	forall i in tb_nr_seq_conta_proc_w.first..tb_nr_seq_conta_proc_w.last
		update	pls_cp_cta_proc_tmp
		set	nr_seq_cp_comb_proc_entao = tb_nr_seq_eserv_w(i)
		where 	nr_seq_conta_proc = tb_nr_seq_conta_proc_w(i);
	commit;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
