CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.gerencia_log_serv ( ds_log_p pls_cp_cta_log_serv.ds_log%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, vl_servico_p pls_cp_cta_log_serv.vl_servico%type, ie_destino_regra_p pls_cp_cta_log_serv.ie_destino_regra%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
-- os campos log e conta proc são obrigatórios
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') and (ds_log_p IS NOT NULL AND ds_log_p::text <> '') then

	current_setting('pls_entao_regra_preco_cta_pck.tb_ds_log_w')::pls_util_cta_pck.t_varchar2_table_4000(current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer) := ds_log_p;
	current_setting('pls_entao_regra_preco_cta_pck.tb_nr_seq_conta_proc_log_w')::pls_util_cta_pck.t_number_table(current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer) := nr_seq_conta_proc_p;
	current_setting('pls_entao_regra_preco_cta_pck.tb_vl_servico_log_w')::pls_util_cta_pck.t_number_table(current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer) := coalesce(vl_servico_p, 0);
	current_setting('pls_entao_regra_preco_cta_pck.tb_dt_atualizacao_log_w')::pls_util_cta_pck.t_date_table(current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer) := clock_timestamp();
	
	-- se atingiu a quantidade manda pro banco
	if (current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer >= pls_util_pck.qt_registro_transacao_w) then
	
		CALL pls_entao_regra_preco_cta_pck.atualizar_log_serv(nm_usuario_p, ie_destino_regra_p);
	-- senão incrementa o contador
	else
	
		PERFORM set_config('pls_entao_regra_preco_cta_pck.nr_cont_log_w', current_setting('pls_entao_regra_preco_cta_pck.nr_cont_log_w')::integer + 1, false);
	end if;
