CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_congenere_a900 ( nr_seq_segurado_p pls_conta.nr_seq_segurado%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_congenere_w	pls_segurado.nr_seq_congenere%type;


BEGIN

nr_seq_congenere_w := null;

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then

	select 	max(nr_seq_congenere)
	into STRICT 	nr_seq_congenere_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;
end if;

-- se não encontrou dados da congenere no cadastro do beneficiário ou não foi passado o beneficiário
-- verifica na fatura atravéz da conta mat
if (coalesce(nr_seq_congenere_w::text, '') = '') then

	select	max(cong.nr_sequencia)
	into STRICT	nr_seq_congenere_w
	from	pls_conta_mat mat,
		pls_conta conta,
		ptu_fatura fat,
		pls_congenere cong
	where	mat.nr_sequencia = nr_seq_conta_mat_p
	and	conta.nr_sequencia = mat.nr_seq_conta
	and	fat.nr_sequencia = conta.nr_seq_fatura
	and	cong.cd_cooperativa = fat.cd_unimed_origem;
end if;

return nr_seq_congenere_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
