CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.obter_dados_tuss_cbhpm ( cd_procedimento_p procedimento.cd_procedimento%type, cd_edicao_amb_p preco_tuss.cd_edicao_amb%type, dt_referencia_p pls_conta_proc.dt_procedimento_referencia%type, cd_porte_p INOUT preco_tuss.cd_porte_cbhpm%type, tx_porte_p INOUT preco_tuss.tx_porte%type, qt_uco_p INOUT preco_tuss.qt_uco%type, nr_porte_anest_p INOUT preco_tuss.nr_porte_anest_cbhpm%type, nr_auxiliar_p INOUT preco_tuss.nr_auxiliares_cbhpm%type, qt_filme_p INOUT preco_tuss.qt_filme_cbhpm%type, qt_incidencia_p INOUT preco_tuss.qt_incidencia_cbhpm%type) AS $body$
DECLARE


C01 CURSOR(	cd_procedimento_pc	procedimento.cd_procedimento%type,
		cd_edicao_amb_pc	preco_tuss.cd_edicao_amb%type,
		dt_referencia_pc	pls_conta_proc.dt_procedimento_referencia%type) FOR
	SELECT	substr(cd_porte_cbhpm, 1, 8) cd_porte_cbhpm,
		coalesce(tx_porte, 1) tx_porte,
		coalesce(qt_uco, 0) qt_uco,
		coalesce(nr_porte_anest_cbhpm, 0) nr_porte_anest_cbhpm,
		coalesce(nr_auxiliares_cbhpm, 0) nr_auxiliares_cbhpm,
		coalesce(qt_filme_cbhpm, 0) qt_filme_cbhpm,
		coalesce(qt_incidencia_cbhpm, 0) qt_incidencia_cbhpm
	from	preco_tuss
	where	cd_procedimento = cd_procedimento_pc
	and	cd_edicao_amb = cd_edicao_amb_pc
	and	dt_referencia_pc between dt_inicio_vigencia_ref and dt_fim_vigencia_ref
	order by dt_inicio_vigencia desc;

BEGIN

-- encontra os dados TUSS CBHPM que serão utilizados para encontrar os valores base
for r_c01_w in C01(	cd_procedimento_p, cd_edicao_amb_p, dt_referencia_p) loop

	-- o primeiro registro a retornar é o correto (mais atual)
	cd_porte_p := r_c01_w.cd_porte_cbhpm;
	tx_porte_p := r_c01_w.tx_porte;
	qt_uco_p := r_c01_w.qt_uco;
	nr_porte_anest_p := r_c01_w.nr_porte_anest_cbhpm;
	nr_auxiliar_p := r_c01_w.nr_auxiliares_cbhpm;
	qt_filme_p := r_c01_w.qt_filme_cbhpm;
	qt_incidencia_p := r_c01_w.qt_incidencia_cbhpm;
	
	-- sai do for e fecha o cursor
	exit;
end loop;

END;

$body$
