CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_mat_unimed_a900 ( nr_seq_material_p pls_conta_mat.nr_seq_material%type) RETURNS bigint AS $body$
DECLARE


nr_seq_mat_unimed_w	pls_mat_unimed_fed_sc.nr_sequencia%type;


BEGIN

nr_seq_mat_unimed_w := null;

-- tenta buscar o material unimed da pls material
select	max(nr_seq_material_unimed)
into STRICT	nr_seq_mat_unimed_w
from	pls_material
where	nr_sequencia = nr_seq_material_p;

-- se n√£o encontrou na pls_material tenta na material unimed
if (coalesce(nr_seq_mat_unimed_w::text, '') = '') then
	
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_mat_unimed_w
	from	pls_material_a900 a,
		pls_material_unimed b
	where	a.nr_seq_material = nr_seq_material_p
	and	b.cd_material = a.cd_material_a900;
end if;

return nr_seq_mat_unimed_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
