CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_origem_edicao ( cd_edicao_amb_p pls_cp_cta_eproc.cd_edicao_amb%type, ie_origem_proced_p pls_conta_proc.ie_origem_proced%type, ie_calculo_tuss_p out edicao_amb.ie_calculo_tuss%type) AS $body$
DECLARE


ie_origem_proced_w	edicao_amb.ie_origem_proced%type;
ie_calculo_tuss_w	edicao_amb.ie_calculo_tuss%type;
				

BEGIN
-- inicia as variáveis como null
ie_origem_proced_w := null;
ie_calculo_tuss_w := null;

-- se tem edicao informado vai atrás
if (cd_edicao_amb_p IS NOT NULL AND cd_edicao_amb_p::text <> '') then

	select	max(ie_origem_proced),
		max(ie_calculo_tuss)
	into STRICT	ie_origem_proced_w,
		ie_calculo_tuss_w
	from	edicao_amb
	where	cd_edicao_amb = cd_edicao_amb_p;
end if;

-- caso não tenha encontrado ou não tenha sido passado a edição, então recebe o que foi passado como origem
if (coalesce(ie_origem_proced_w::text, '') = '') then

	ie_origem_proced_w := ie_origem_proced_p;
end if;

-- atribui o valor a variável out
ie_calculo_tuss_p := ie_calculo_tuss_w;

return;

END;

$body$
