CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_porte_anest_tuss ( nr_porte_anest_p preco_tuss.nr_porte_anest_cbhpm%type) RETURNS varchar AS $body$
DECLARE


cd_porte_anestesista_w	pls_porte_anestesico_tuss.cd_porte_cbhpm%type;


BEGIN
-- inicia nulo
cd_porte_anestesista_w := null;

if (nr_porte_anest_p IS NOT NULL AND nr_porte_anest_p::text <> '') then

	-- busca o código do porte
	select	max(cd_porte_cbhpm)
	into STRICT	cd_porte_anestesista_w
	from 	pls_porte_anestesico_tuss
	where	nr_porte_anestesico = nr_porte_anest_p;

	-- caso não tenha encontrado define neste local
	if (coalesce(cd_porte_anestesista_w::text, '') = '') then
		-- Define porte do anestesista
		case(nr_porte_anest_p)

			when 1 then
				cd_porte_anestesista_w := '3A';

			when 2 then
				cd_porte_anestesista_w := '3C';

			when 3 then
				cd_porte_anestesista_w := '4C';

			when 4 then
				cd_porte_anestesista_w := '6B';

			when 5 then
				cd_porte_anestesista_w := '7C';

			when 6 then
				cd_porte_anestesista_w := '9B';

			when 7 then
				cd_porte_anestesista_w := '10C';

			when 8 then
				cd_porte_anestesista_w := '12A';

			else
				cd_porte_anestesista_w := null;
		end case;
	end if;
end if;

return cd_porte_anestesista_w;

END;

$body$
