CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_preco_mat_forn_adic ( cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_material_p pls_material.nr_sequencia%type, dt_vigencia_p timestamp, tx_ajuste_tab_p pls_cp_cta_emat.tx_ajuste_tab_propria%type, nr_seq_fornecedor_p pls_prestador.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


vl_preco_venda_w	pls_material_fornec_preco.vl_preco%type;
nr_seq_fornecedor_w	pls_prestador.nr_sequencia%type;

C01 CURSOR(	nr_seq_fornecedor_pc	pls_material_fornec.nr_seq_fornecedor%type,
		dt_vigencia_pc		timestamp,
		cd_estabelecimento_pc	estabelecimento.cd_estabelecimento%type,
		nr_seq_material_pc	pls_material.nr_sequencia%type) FOR
	SELECT	b.vl_preco
	from	pls_material_fornec a,
		pls_material_fornec_preco b
	where	a.nr_seq_material = nr_seq_material_pc
	and	a.nr_seq_fornecedor = nr_seq_fornecedor_pc
	and	b.nr_seq_mat_fornec = a.nr_sequencia
	and	dt_vigencia_pc between b.dt_inicio_vigencia_ref and b.dt_fim_vigencia_ref
	and 	exists (	SELECT 1
			from	pls_material x
			where	x.nr_sequencia = nr_seq_material_pc
			and	x.cd_estabelecimento = cd_estabelecimento_pc)
	order by b.dt_inicio_vigencia desc;

BEGIN
-- Obter a sequencia do prestador/fornecedor
select	max(b.nr_sequencia)
into STRICT	nr_seq_fornecedor_w
from 	pls_prestador a,
	pls_fornecedor_material b
where 	a.nr_sequencia = nr_seq_fornecedor_p
and	b.cd_cgc = a.cd_cgc;

for r_c01_w in C01(	nr_seq_fornecedor_w, dt_vigencia_p, cd_estabelecimento_p,
			nr_seq_material_p) loop

vl_preco_venda_w := r_c01_w.vl_preco;
-- retorna o primeiro registro que encontrar ap√≥s sai do loop e fecha o cursor automaticamente
exit;			
end loop;

-- se for null no preco ou na taxa retorna zero
vl_preco_venda_w := coalesce(vl_preco_venda_w, 0) * coalesce(tx_ajuste_tab_p, 0);

return vl_preco_venda_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
