CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_se_grupo_preco_mat ( nr_seq_grupo_material_p pls_preco_material.nr_seq_grupo%type, nr_seq_material_p pls_preco_material.nr_seq_material%type) RETURNS varchar AS $body$
DECLARE


ie_valido_w	varchar(1);
qt_regra_w	integer;


BEGIN
-- começa inválido
ie_valido_w := 'N';

select 	count(1)
into STRICT	qt_regra_w
from	pls_preco_material b,
	pls_preco_grupo_material c
where 	b.nr_seq_material = nr_seq_material_p
and	b.nr_seq_grupo = nr_seq_grupo_material_p
and	c.nr_sequencia = b.nr_seq_grupo
and	c.ie_situacao = 'A';

-- se não encontrou nenhum registro não é válido
if (qt_regra_w > 0) then
	
	ie_valido_w := 'S';
end if;

return	ie_valido_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
