CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_valor_anest_cbhpm ( ie_ch_padrao_anest_p pls_cp_cta_eproc.ie_ch_padrao_anestesista%type, vl_moeda_anestesista_p pls_cp_cta_eproc.cd_moeda_anestesista%type, vl_porte_anest_cbhpm_p cbhpm_porte.vl_porte%type, tx_ajuste_partic_p pls_cp_cta_eproc.tx_ajuste_partic%type, vl_tx_honorarios_p pls_cp_cta_eproc.vl_ch_honorarios%type) RETURNS bigint AS $body$
DECLARE


vl_anestesista_w	pls_conta_proc.vl_anestesista%type;


BEGIN

case(ie_ch_padrao_anest_p)
	-- Anestesista
	when 'C' then
		-- se tem moeda informada aplica
		if (vl_moeda_anestesista_p > 0 ) then
		
			vl_anestesista_w := vl_porte_anest_cbhpm_p * vl_moeda_anestesista_p * tx_ajuste_partic_p;
		else
		
			vl_anestesista_w := vl_porte_anest_cbhpm_p * tx_ajuste_partic_p;
		end if;
	-- MÃ©dico
	when 'M' then

		vl_anestesista_w := vl_porte_anest_cbhpm_p * tx_ajuste_partic_p * vl_tx_honorarios_p;
	-- Ambos
	when 'A' then

		-- se foi informado moeda para anestesista aplica
		if (vl_moeda_anestesista_p > 0 ) then

			vl_anestesista_w := vl_porte_anest_cbhpm_p * vl_moeda_anestesista_p * tx_ajuste_partic_p * vl_tx_honorarios_p;
		else

			vl_anestesista_w := vl_porte_anest_cbhpm_p * tx_ajuste_partic_p * vl_tx_honorarios_p;
		end if;
	else
		null;
end case;

return vl_anestesista_w;

