CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_valor_aux_cbhpm ( vl_medico_p pls_conta_proc.vl_medico%type, nr_auxiliares_p cbhpm_preco.nr_auxiliar%type) RETURNS bigint AS $body$
DECLARE


vl_auxiliares_w		pls_conta_proc.vl_auxiliares%type;


BEGIN
vl_auxiliares_w := 0;

-- se possui auxiliares faz o calculo
if (nr_auxiliares_p > 0) then
	-- Para CBHPM sempre paga 30% para o 1ยบ auxiliar, os demais 20%
	vl_auxiliares_w	:= (vl_medico_p * 0.3) + (vl_medico_p * (nr_auxiliares_p -1) * 0.2);
end if;

return vl_auxiliares_w;

END;

$body$
