CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_valor_material_espec ( nr_seq_guia_p pls_solic_lib_mat_med.nr_seq_guia%type, nr_seq_material_p pls_solic_lib_mat_med.nr_seq_material%type) RETURNS bigint AS $body$
DECLARE


vl_material_w	pls_solic_lib_mat_med.vl_unit_aprovado%type;


BEGIN

select	max(vl_unit_aprovado)
into STRICT	vl_material_w
from	pls_solic_lib_mat_med
where	nr_seq_material	= nr_seq_material_p
and	nr_seq_guia = nr_seq_guia_p
and	ie_status = 3;

-- caso n√£o encontre valor retorna 0 (zero)
return coalesce(vl_material_w,0);

END;

