CREATE OR REPLACE FUNCTION pls_entao_regra_preco_cta_pck.obter_valor_tabela_serv ( cd_tabela_servico_p pls_cp_cta_eserv.cd_tabela_servico%type, cd_procedimento_p pls_conta_proc.cd_procedimento%type, ie_origem_proced_p pls_conta_proc.ie_origem_proced%type, dt_proced_referencia_p pls_conta_proc.dt_procedimento_referencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


vl_servico_w		preco_servico.vl_servico%type;

C01 CURSOR(	cd_tabela_servico_pc	pls_cp_cta_eserv.cd_tabela_servico%type,
		cd_procedimento_pc	pls_conta_proc.cd_procedimento%type,
		ie_origem_proced_pc	pls_conta_proc.ie_origem_proced%type,
		cd_estabelecimento_pc	estabelecimento.cd_estabelecimento%type,
		dt_proced_referencia_pc	pls_conta_proc.dt_procedimento_referencia%type) FOR
	SELECT	vl_servico
	from	preco_servico
	where	cd_tabela_servico = cd_tabela_servico_pc
	and	cd_procedimento	= cd_procedimento_pc
	and	ie_origem_proced = ie_origem_proced_pc
	and	cd_estabelecimento = cd_estabelecimento_pc
	and	dt_proced_referencia_pc between dt_inicio_vigencia
					and dt_fim_vigencia_ref
	order by dt_inicio_vigencia desc;
					
BEGIN
-- inicia a variavel
vl_servico_w := 0;

-- só processa caso a tabela esteja informada
if (cd_tabela_servico_p IS NOT NULL AND cd_tabela_servico_p::text <> '') then	

	-- retorna o valor do serviço mais atual de acordo com a tabela
	for r_c01_w in C01(	cd_tabela_servico_p, cd_procedimento_p,
				ie_origem_proced_p, cd_estabelecimento_p,
				dt_proced_referencia_p) loop
		
		vl_servico_w := r_c01_w.vl_servico;
		
		-- sai do loop e fecha o cursor
		exit;
	end loop;
end if;

return vl_servico_w;

END;

$body$
