SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_entao_regra_preco_cta_pck.obter_valores_cbhpm_partic ( ie_destino_regra_p text, nr_seq_cbhpm_edicao_p pls_cp_cta_epartic.nr_seq_cbhpm_edicao%type, vl_tx_honorarios_p pls_cp_cta_epartic.vl_ch_honorarios%type, tx_ajuste_ch_honor_p pls_cp_cta_epartic.tx_ajuste_ch_honor%type, tx_ajuste_partic_p pls_cp_cta_epartic.tx_ajuste_partic%type, dt_proced_referencia_p pls_conta_proc.dt_procedimento_referencia%type, cd_procedimento_p pls_conta_proc.cd_procedimento%type, ie_origem_proc_p pls_conta_proc.ie_origem_proced%type, cd_especialidade_p grupo_proc.cd_especialidade%type, cd_grupo_proc_p procedimento.cd_grupo_proc%type, cd_area_proced_p especialidade_proc.cd_area_procedimento%type, nr_seq_proc_partic_p pls_proc_participante.nr_sequencia%type, nr_seq_prestador_exec_p pls_conta.nr_seq_prestador_exec%type, ie_ch_padrao_anest_p pls_cp_cta_epartic.ie_ch_padrao_anestesista%type, vl_moeda_anest_p pls_cp_cta_epartic.cd_moeda_filme%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, vl_negociado_p INOUT bigint, vl_medico_out_p INOUT bigint, vl_anestesista_out_p INOUT bigint, vl_auxiliares_out_p INOUT bigint) AS $body$
DECLARE


dt_inicio_vig_edicao_w	cbhpm_edicao.dt_vigencia%type;
qt_uco_cbhpm_w 		cbhpm_preco.qt_uco%type;
vl_medico_cbhpm_w	pls_conta_proc.vl_medico%type;
qt_filme_cbhpm_w	cbhpm_preco.qt_filme%type;
nr_porte_anest_cbhpm_w	cbhpm_preco.nr_porte_anest%type;
nr_auxiliar_cbhpm_w	cbhpm_preco.nr_auxiliar%type;
vl_auxiliares_w		pls_conta_proc.vl_auxiliares%type;
vl_anestesista_w	pls_conta_proc.vl_anestesista%type;
vl_porte_anest_cbhpm_w	cbhpm_porte.vl_porte%type;
vl_procedimento_w	pls_conta_proc.vl_procedimento%type;
vl_medico_w		pls_conta_proc.vl_medico%type;


BEGIN
-- pega a data da edição CBHPM, se não encontrar retorna a data do procedimento
select	coalesce(max(dt_vigencia), dt_proced_referencia_p)
into STRICT	dt_inicio_vig_edicao_w
from	cbhpm_edicao
where	nr_sequencia = nr_seq_cbhpm_edicao_p;

-- obtém os valores da tabela CBHPM
SELECT * FROM pls_entao_regra_preco_cta_pck.obter_preco_cbhpm(	cd_procedimento_p, ie_origem_proc_p, dt_proced_referencia_p, dt_inicio_vig_edicao_w, nr_seq_prestador_exec_p, cd_grupo_proc_p, cd_especialidade_p, cd_area_proced_p, cd_estabelecimento_p, vl_medico_cbhpm_w, qt_uco_cbhpm_w, nr_porte_anest_cbhpm_w, nr_auxiliar_cbhpm_w, qt_filme_cbhpm_w, vl_porte_anest_cbhpm_w) INTO STRICT vl_medico_cbhpm_w, qt_uco_cbhpm_w, nr_porte_anest_cbhpm_w, nr_auxiliar_cbhpm_w, qt_filme_cbhpm_w, vl_porte_anest_cbhpm_w;

CALL pls_entao_regra_preco_cta_pck.gerencia_log_partic(	'Valores obtidos na tabela de preços CBHPM.',
			nr_seq_proc_partic_p,
			null,
			null,
			vl_medico_cbhpm_w,
			null,
			ie_destino_regra_p,
			nm_usuario_p);

-- aplica as taxas pertinentes a CBHPM
SELECT * FROM pls_entao_regra_preco_cta_pck.aplica_taxas_cbhpm_partic(	vl_medico_cbhpm_w, vl_tx_honorarios_p, tx_ajuste_ch_honor_p, vl_medico_w, nr_porte_anest_cbhpm_w, nr_auxiliar_cbhpm_w) INTO STRICT vl_medico_w, nr_porte_anest_cbhpm_w, nr_auxiliar_cbhpm_w;

CALL pls_entao_regra_preco_cta_pck.gerencia_log_partic(	'Aplicado taxas e cotação de moedas.',
			nr_seq_proc_partic_p,
			null,
			null,
			vl_medico_w,
			null,
			ie_destino_regra_p,
			nm_usuario_p);

-- obtém o valor dos auxiliares
vl_auxiliares_w := pls_entao_regra_preco_cta_pck.obter_valor_aux_cbhpm( 	vl_medico_w, nr_auxiliar_cbhpm_w);

-- obtém o valor do anestesista
vl_anestesista_w := pls_entao_regra_preco_cta_pck.obter_valor_anest_cbhpm(	ie_ch_padrao_anest_p, vl_moeda_anest_p, vl_porte_anest_cbhpm_w,
						tx_ajuste_partic_p, vl_tx_honorarios_p);

-- calcula o valor do procedimento
vl_procedimento_w := pls_entao_regra_preco_cta_pck.obter_valor_proced( 	null, vl_anestesista_w, vl_medico_w,
						null, vl_auxiliares_w);

CALL pls_entao_regra_preco_cta_pck.gerencia_log_partic(	'Obtidos valores dos auxiliares e anestesista. Realizado cálculo do valor do negociado.',
			nr_seq_proc_partic_p,
			vl_anestesista_w,
			vl_auxiliares_w,
			vl_medico_w,
			vl_procedimento_w,
			ie_destino_regra_p,
			nm_usuario_p);

-- alimenta os parametros out
vl_negociado_p := vl_procedimento_w;
vl_medico_out_p	:= vl_medico_w;
vl_anestesista_out_p := vl_anestesista_w;
vl_auxiliares_out_p := vl_auxiliares_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
