SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_env_aviso_ven_doc_pres_pck.aceitar_doc_notific_portal ( nr_seq_prestador_doc_p pls_prestador_documento.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Aceitar o documento enviado via portal
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w			integer;
ie_possui_pendencia_w		varchar(1);
nr_seq_prest_doc_venc_w		pls_prestador_doc_venc.nr_sequencia%type;
nr_seq_prest_tipo_doc_w		pls_prestador_doc_venc.nr_seq_prest_tipo_doc%type;


BEGIN

-- So insere se tiver os dados informados
if	(nr_seq_prestador_doc_p IS NOT NULL AND nr_seq_prestador_doc_p::text <> '' AND nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update	pls_prestador_documento
	set	ie_status	= 'A',
		dt_aceite	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_prestador_doc_p;
	
	ie_possui_pendencia_w := pls_env_aviso_ven_doc_pres_pck.possui_notific_pendente(null, nr_seq_prestador_doc_p);
	
	if (ie_possui_pendencia_w = 'N') then
	
		select	max(a.nr_seq_prest_doc_venc),
			max(a.nr_seq_prest_tipo)
		into STRICT	nr_seq_prest_doc_venc_w,
			nr_seq_prest_tipo_doc_w
		from	pls_prestador_documento	a
		where	a.nr_sequencia	= nr_seq_prestador_doc_p;
		
		-- Liberacao de registro manual
		if (coalesce(nr_seq_prest_doc_venc_w::text, '') = '') then
			select	max(nr_sequencia)
			into STRICT	nr_seq_prest_doc_venc_w
			from	pls_prestador_doc_venc
			where	nr_seq_prest_tipo_doc	= nr_seq_prest_tipo_doc_w
			and	ie_status		= 'P';
			
			-- Registro manual nao possui vinculo com o portal
			if (nr_seq_prest_doc_venc_w IS NOT NULL AND nr_seq_prest_doc_venc_w::text <> '') then
				update	pls_prestador_documento
				set	nr_seq_prest_doc_venc	= nr_seq_prest_doc_venc_w
				where	nr_sequencia		= nr_seq_prestador_doc_p;
			end if;
		end if;
		
		CALL pls_env_aviso_ven_doc_pres_pck.atualizar_status_not_portal(nr_seq_prest_doc_venc_w, 'A', nm_usuario_p);
	end if;
		
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_env_aviso_ven_doc_pres_pck.aceitar_doc_notific_portal ( nr_seq_prestador_doc_p pls_prestador_documento.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
