CREATE OR REPLACE PROCEDURE pls_env_aviso_ven_doc_pres_pck.gerar_notificacao_portal ( nr_seq_doc_p pls_prestador_tipo_doc.nr_sequencia%type, ds_observacao_p pls_prestador_doc_venc.ds_observacao%type, nr_seq_regra_prest_dest_p pls_regra_com_prest_dest.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Gerar as notificacoes que ficarao disponiveis no portal, por prestador
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_sequencia_w	pls_prestador_doc_venc.nr_sequencia%type;


BEGIN

-- So insere se tiver os dados informados
if	(nr_seq_doc_p IS NOT NULL AND nr_seq_doc_p::text <> '' AND nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	nextval('pls_prestador_doc_venc_seq')
