CREATE OR REPLACE PROCEDURE pls_env_aviso_ven_doc_pres_pck.gravar_doc_notif_portal ( nr_seq_prest_doc_venc_p pls_prestador_doc_venc.nr_sequencia%type, ds_arquivo_p pls_prestador_documento.ds_arquivo%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Grava os documentos enviados pelo prestador
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

-- So insere se tiver os dados informados
if	((nr_seq_prest_doc_venc_p IS NOT NULL AND nr_seq_prest_doc_venc_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ds_arquivo_p IS NOT NULL AND ds_arquivo_p::text <> '')) then

	insert into pls_prestador_documento(	nr_sequencia,
						nr_seq_prest_doc_venc,
						nr_seq_prest_tipo,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
