	SELECT	nextval('pls_prestador_documento_seq'),
		nr_seq_prest_doc_venc_p,
		nr_seq_prest_tipo_doc,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		'P',
		null,
