CREATE OR REPLACE FUNCTION pls_env_aviso_ven_doc_pres_pck.obter_dados_doc_venc ( nr_seq_prest_doc_venc_p pls_prestador_doc_venc.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna os dados pertinentes ao documento de notificacao gerada para o prestador
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
 
 OPCOES
'ST'	- Status
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(500) := '';

BEGIN

if (ie_opcao_p = 'ST') then

	select	max(ie_status)
	into STRICT	ds_retorno_w
	from	pls_prestador_doc_venc
	where	nr_sequencia	= nr_seq_prest_doc_venc_p;
	
end if;

return ds_retorno_w;

END;

$body$
