CREATE OR REPLACE FUNCTION pls_env_aviso_ven_doc_pres_pck.obter_dados_prest_tipo_doc ( nr_seq_prest_tipo_doc_p pls_prestador_tipo_doc.nr_sequencia%type, nr_seq_regra_com_prest_dest_p pls_regra_com_prest_dest.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna os dados pertinentes ao documento
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
 
 OPCOES
'NT'	- Possui Notificacao pendente
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(500) := '';
qt_registro_w	integer;


BEGIN

if (ie_opcao_p = 'NT') then

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_prestador_doc_venc
	where	nr_seq_prest_tipo_doc	= nr_seq_prest_tipo_doc_p
	and		nr_seq_regra_prest_dest = nr_seq_regra_com_prest_dest_p
	and		ie_status		= 'P'; -- Pendentes
	
	if (qt_registro_w = 0) then
	
		ds_retorno_w := 'N';
	else
	
		ds_retorno_w := 'S';
	end if;

return ds_retorno_w;

end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
