CREATE OR REPLACE FUNCTION pls_env_aviso_ven_doc_pres_pck.obter_dados_prest_venc ( nr_seq_prestador_p pls_prestador_tipo_doc.nr_seq_prestador%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna os dados pertinentes a notificacao gerada para o prestador
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
 
 OPCOES
'NP'	- Prestador possui notificacao pendente
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w	integer;

ds_retorno_w	varchar(500) := '';

BEGIN

-- Prestador possui notificacao pendente
if (ie_opcao_p = 'NP') then

	select	count(1)
	into STRICT	qt_registro_w
	from	pls_prestador_doc_venc	a,
		pls_prestador_tipo_doc	b
	where	b.nr_sequencia		= a.nr_seq_prest_tipo_doc
	and	b.nr_seq_prestador	= nr_seq_prestador_p
	and	a.ie_status		= 'P';
	
	if (qt_registro_w > 0) then
	
		ds_retorno_w	:= 'S';
	else
	
		ds_retorno_w	:= 'N';
	end if;	
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
