CREATE OR REPLACE FUNCTION pls_env_aviso_ven_doc_pres_pck.possui_notific_login_web ( nr_seq_usuario_web_p pls_usuario_web.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna se possui notificacao pendente para o login web como um todo
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w	integer;
ds_retorno_w	varchar(1);

BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_prestador_tipo_doc		a,
	pls_prestador_doc_venc		b,
	pls_prestador_usuario_web	c
where	b.nr_seq_prest_tipo_doc	= a.nr_sequencia
and	c.nr_seq_prestador	= a.nr_seq_prestador
and	c.nr_seq_usuario	= nr_seq_usuario_web_p
and	b.ie_status		= 'P';

if (qt_registro_w = 0) then
	
	ds_retorno_w := 'N';
else

	ds_retorno_w := 'S';
end if;

return ds_retorno_w;
end;



$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
