CREATE OR REPLACE FUNCTION pls_env_aviso_ven_doc_pres_pck.possui_notific_pendente ( nr_seq_prest_doc_venc_p pls_prestador_doc_venc.nr_sequencia%type, nr_seq_prest_doc_p pls_prestador_documento.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna se possui notificacao pendente, por notificacao ou anexo ja enviado
---------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [  ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------
 Alteracoes:	
 ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_prest_doc_venc_w	pls_prestador_doc_venc.nr_sequencia%type;
qt_registro_w		integer;
ds_retorno_w		varchar(1);


BEGIN

if (coalesce(nr_seq_prest_doc_venc_p::text, '') = '') then
	select	max(a.nr_seq_prest_doc_venc)
	into STRICT	nr_seq_prest_doc_venc_w
	from	pls_prestador_documento	a
	where	a.nr_sequencia	= nr_seq_prest_doc_p;
else
	nr_seq_prest_doc_venc_w := nr_seq_prest_doc_venc_p;
end if;

select	count(1)
into STRICT	qt_registro_w
from	pls_prestador_documento
where	nr_seq_prest_doc_venc	= nr_seq_prest_doc_venc_w
and	ie_status	= 'P';

if (qt_registro_w = 0) then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
