CREATE OR REPLACE PROCEDURE pls_envio_ans_extra_pck.atualiza_registros_mat ( tb_conta_seq_p pls_util_cta_pck.t_number_table, tb_item_seq_p dbms_sql.number_table, tb_vl_lib_p pls_util_cta_pck.t_number_table, tb_tiss_val_seq_p pls_util_cta_pck.t_number_table, tb_reg_atualizar_p pls_util_cta_pck.t_number_table, ie_tipo_p text) AS $body$
BEGIN

	if ( tb_conta_seq_p.count > 0) then
						
		forall i in tb_conta_seq_p.first..tb_conta_seq_p.last
			update	pls_monitor_tiss_mat_val
			set	vl_liberado = vl_liberado + tb_vl_lib_p(i),
				vl_glosa = vl_glosa - tb_vl_lib_p(i)
			where	nr_sequencia = tb_reg_atualizar_w(i);
		commit;
		
		--exclui registro mat de recurso
		forall i in tb_conta_seq_p.first..tb_conta_seq_p.last
			delete from pls_monitor_tiss_mat_val
			where	nr_sequencia = tb_tiss_val_seq_p(i);
		commit;
		
	end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
