CREATE OR REPLACE PROCEDURE pls_envio_ans_extra_pck.atualiza_tabela_controle ( tb_seq_pc pls_util_cta_pck.t_number_table, tb_seq_conta_pc pls_util_cta_pck.t_number_table, ie_status_pc text) AS $body$
BEGIN

	if (tb_seq_conta_pc.count = 0) then
	
		forall i in tb_seq_pc.first .. tb_seq_pc.last
			update 	pls_monitor_tiss_alt set
				ie_status = ie_status_pc
			where 	nr_sequencia = tb_seq_pc(i);
		commit;
	else
		forall i in tb_seq_pc.first .. tb_seq_pc.last
			update 	pls_monitor_tiss_alt set
				ie_status = ie_status_pc
			where 	nr_seq_conta = tb_seq_conta_pc(i)
			and 	nr_sequencia != tb_seq_pc(i)
			and 	ie_tipo_evento = 'PD'
			and 	ie_status in ('P', 'N');
		commit;
	end if;

end;	


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
