SET client_encoding TO 'UTF8';

------------------------------------------------------------------
-- Finalidade:	Busca por lotes de faturamento, que foram iniciada a sua geracao, mas por algum 
--	motivo a session da geracao foi interrompida, e os tratamentos de exception nao foram executados.
-- Se tem id e serial populado, significa que ja ocorreu uma execucao de geracao do lote e se nao estao populados,
--significa que ainda nao ocorreu geracao ou entao ja ocorreu e foi concluida, por essa razao, caso tenha sid e serial, verificamos,
--se o lote nao esta sendo processado por nenhuma sessao e caso nao esteja, significa que o processo foi abortado
CREATE OR REPLACE FUNCTION pls_envio_ans_extra_pck.retorna_lote_sem_session ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	pls_monitor_tiss_lote.nr_sequencia%type;

BEGIN

-- carrega o sequencial mais recente do lote que foi considerado invalido
select	max(a.nr_sequencia)
into STRICT	ds_retorno_w
from	pls_monitor_tiss_lote	a
where	a.nr_sequencia = nr_seq_lote_p
and	(a.nm_id_serial IS NOT NULL AND a.nm_id_serial::text <> '')
and	(a.nm_id_sid IS NOT NULL AND a.nm_id_sid::text <> '')
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	not exists (	SELECT	1
					from	gv$session	x
					where	x.serial#	= a.nm_id_serial
					and	x.sid		= nm_id_sid);
return ds_retorno_w;
					
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
