CREATE OR REPLACE PROCEDURE pls_execucao_cirurgica_pck.alterar_dados_procedimento ( nr_seq_exec_cirurgica_proc_p pls_exec_cirurgica_proc.nr_sequencia%type, ie_via_acesso_p pls_exec_cirurgica_proc.ie_via_acesso%type, ie_tecnica_utilizada_p pls_exec_cirurgica_proc.ie_tecnica_utilizada%type, vl_apresentado_p pls_exec_cirurgica_proc.vl_apresentado%type, vl_fator_red_acrescimo_p pls_exec_cirurgica_proc.vl_fator_red_acrescimo%type, qt_procedimento_p pls_exec_cirurgica_proc.qt_procedimento%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Alterar os dados de contas medicas dos procedimentos.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_procedimento_w	pls_exec_cirurgica_proc.qt_procedimento%type;
		

BEGIN

if (coalesce(qt_procedimento_p, 0) > 0)then
	qt_procedimento_w := qt_procedimento_p;
else 	
	select 	qt_procedimento
	into STRICT 	qt_procedimento_w
	from 	pls_exec_cirurgica_proc
	where	nr_sequencia	= nr_seq_exec_cirurgica_proc_p;
end if;

update	pls_exec_cirurgica_proc
set	ie_via_acesso		= ie_via_acesso_p,
	ie_tecnica_utilizada	= ie_tecnica_utilizada_p,
	vl_apresentado		= vl_apresentado_p,
	vl_fator_red_acrescimo	= coalesce(vl_fator_red_acrescimo_p, 1),
	qt_procedimento		= qt_procedimento_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_exec_cirurgica_proc_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
