CREATE OR REPLACE PROCEDURE pls_execucao_cirurgica_pck.finalizar_exec_cirurgica ( nr_seq_exec_cirurgica_p pls_execucao_cirurgica.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Finalizar a execucao cirurgica no portal.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_exec_cirurgica_w		pls_execucao_cirurgica.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_execucao_guia
	from	pls_exec_cirurgica_guia
	where	nr_seq_exec_cirurgica	= nr_seq_exec_cirurgica_w
	and	ie_status		<> 'F';
BEGIN

begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_exec_cirurgica_w
	from	pls_execucao_cirurgica a
	where	a.ie_status	<> 'F'
	and 	a.nr_sequencia	= nr_seq_exec_cirurgica_p;
exception
when others then
	nr_seq_exec_cirurgica_w	:= null;
end;

if (nr_seq_exec_cirurgica_w IS NOT NULL AND nr_seq_exec_cirurgica_w::text <> '') then
	for C01_w in C01 loop
		begin
			update	pls_exec_cirurgica_proc
			set	ie_finalizado		= 'S',
				nm_usuario		= nm_usuario_p,	
				dt_atualizacao		= clock_timestamp()
			where	nr_seq_execucao_guia	= C01_w.nr_seq_execucao_guia
			and	ie_finalizado		= 'N';
			
			update	pls_exec_cirurgica_guia
			set	ie_status		= 'F',
				nm_usuario		= nm_usuario_p,	
				dt_atualizacao		= clock_timestamp()
			where	nr_sequencia		= C01_w.nr_seq_execucao_guia;
		end;
	end loop;
		
	update	pls_execucao_cirurgica
	set	ie_status		= 'F',
		nm_usuario		= nm_usuario_p,	
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_exec_cirurgica_w;

	CALL pls_execucao_cirurgica_pck.gerar_log_exec_cirurgica(nr_seq_exec_cirurgica_w, wheb_mensagem_pck.get_texto(1147519,'NM_USUARIO=' || nm_usuario_p), cd_estabelecimento_p, nm_usuario_p);
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
