SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_execucao_cirurgica_pck.inserir_procedimento_exec ( nr_seq_exec_cirurgica_p pls_execucao_cirurgica.nr_sequencia%type, nr_seq_lanc_proc_exec_p pls_regra_lanc_proc_exec.nr_sequencia%type, nr_seq_exec_cirurg_proc_sub_p pls_exec_cirurgica_proc.nr_sequencia%type, nr_seq_usuario_web_p pls_usuario_web.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, qt_procedimento_p pls_requisicao_proc.qt_solicitado%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir novo procedimento ou procedimento substitudo na execucao cirurgica.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_exec_cirurg_proc_w		pls_exec_cirurgica_proc.nr_sequencia%type;
ie_origem_proced_w			procedimento.ie_origem_proced%type;
cd_procedimento_w			procedimento.cd_procedimento%type;


BEGIN

if (nr_seq_lanc_proc_exec_p IS NOT NULL AND nr_seq_lanc_proc_exec_p::text <> '') then
	begin
		select	ie_origem_proced,
			cd_procedimento
		into STRICT	ie_origem_proced_w,
			cd_procedimento_w
		from	pls_regra_lanc_proc_exec
		where	nr_sequencia	= nr_seq_lanc_proc_exec_p
		and	ie_situacao	= 'A'
		and (coalesce(dt_fim_vigencia::text, '') = ''
		or	clock_timestamp()	between dt_inicio_vigencia and fim_dia(dt_fim_vigencia));
	exception
	when others then
		ie_origem_proced_w	:= null;
	end;
	
	if (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') and (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then				
		CALL pls_execucao_cirurgica_pck.gerar_item_complemento(nr_seq_requisicao_p, ie_origem_proced_w, cd_procedimento_w, qt_procedimento_p, nm_usuario_p, cd_estabelecimento_p);
	end if;
end if;

if (nr_seq_exec_cirurg_proc_sub_p IS NOT NULL AND nr_seq_exec_cirurg_proc_sub_p::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_exec_cirurg_proc_w
	from	pls_exec_cirurgica_proc a,
		pls_exec_cirurgica_guia b
	where	a.nr_seq_execucao_guia	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_exec_cirurg_proc_sub_p
	and	b.ie_status		<> 'F';
	
	if (nr_seq_exec_cirurg_proc_w IS NOT NULL AND nr_seq_exec_cirurg_proc_w::text <> '') then
		update	pls_exec_cirurgica_proc
		set	ie_situacao_item	= 'S',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_exec_cirurg_proc_w;
		
		--Substituicao do procedimento sequencia #@NR_SEQ_EXEC_CIRURG_PROC_SUB#@ pelo usuario #@NM_USUARIO#@.
		CALL pls_execucao_cirurgica_pck.gerar_log_exec_cirurgica(nr_seq_exec_cirurgica_p, wheb_mensagem_pck.get_texto(1148193,'NR_SEQ_EXEC_CIRURG_PROC_SUB=' || nr_seq_exec_cirurg_proc_sub_p || ';' || 'NM_USUARIO=' || nm_usuario_p), cd_estabelecimento_p, nm_usuario_p);
	end if;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_execucao_cirurgica_pck.inserir_procedimento_exec ( nr_seq_exec_cirurgica_p pls_execucao_cirurgica.nr_sequencia%type, nr_seq_lanc_proc_exec_p pls_regra_lanc_proc_exec.nr_sequencia%type, nr_seq_exec_cirurg_proc_sub_p pls_exec_cirurgica_proc.nr_sequencia%type, nr_seq_usuario_web_p pls_usuario_web.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, qt_procedimento_p pls_requisicao_proc.qt_solicitado%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
