CREATE OR REPLACE FUNCTION pls_execucao_cirurgica_pck.obter_se_guia_execucao ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type) RETURNS SETOF T_GUIA AS $body$
DECLARE

			
line_w		t_guia_row;
			
c01 CURSOR FOR
	--Guia internacao sem execucao cirurgica
	SELECT	'S' ie_iniciar,
		'N' ie_reabrir,
		a.nr_sequencia nr_seq_guia_principal
	from	pls_guia_plano a
	where	a.nr_sequencia = nr_seq_guia_p
	and	a.ie_tipo_guia = '1'
	and	a.ie_estagio in (5, 6, 10)
	and	not exists (	SELECT	1
				from	pls_execucao_cirurgica b
				where	b.nr_seq_guia = a.nr_sequencia)
	
union

	--Guia de complemento que a guia principal nao teve execucao cirurgica (pequenas cirurgias)
	select	'S' ie_iniciar,
		'N' ie_reabrir,
		a.nr_sequencia nr_seq_guia_principal
	from	pls_guia_plano a
	where	a.nr_sequencia = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and	a.ie_tipo_atend_tiss = 2
	and	a.ie_estagio in (5, 6, 10)
	and	not exists (	select	1
				from	pls_execucao_cirurgica b
				where	b.nr_seq_guia = a.nr_sequencia)
	
union

	--Guia de complemento que a guia principal nao teve execucao cirurgica
	select	'S' ie_iniciar,
		'N' ie_reabrir,
		a.nr_seq_guia_principal nr_seq_guia_principal
	from	pls_guia_plano a
	where	a.nr_sequencia = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and (a.ie_tipo_atend_tiss = 7 or coalesce(a.ie_regime_atendimento,9) = 3)
	and	a.ie_estagio in (5, 6, 10)
	and	(a.nr_seq_guia_principal IS NOT NULL AND a.nr_seq_guia_principal::text <> '')
	and	not exists (	select	1
				from	pls_execucao_cirurgica b
				where	b.nr_seq_guia = a.nr_seq_guia_principal)
	
union

	--Guia princial que possui complemento ja executada
	select	'N' ie_iniciar,
		'S' ie_reabrir,
		a.nr_seq_guia_principal nr_seq_guia_principal
	from	pls_guia_plano a
	where	a.nr_seq_guia_principal = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and (a.ie_tipo_atend_tiss = 7 or coalesce(a.ie_regime_atendimento,9) = 3)
	and	a.ie_estagio in (5, 6, 10)
	and	(a.nr_seq_guia_principal IS NOT NULL AND a.nr_seq_guia_principal::text <> '')
	and	not exists (	select	1
				from	pls_exec_cirurgica_guia b
				where	b.nr_seq_guia = a.nr_sequencia)
	and	exists (	select	1
				from	pls_execucao_cirurgica c
				where	c.nr_seq_guia	= a.nr_seq_guia_principal
				and	c.ie_faturado	= 'N')
	
union

	--Guia complemento com principal ja executada
	select	'N' ie_iniciar,
		'S' ie_reabrir,
		a.nr_seq_guia_principal nr_seq_guia_principal
	from	pls_guia_plano a
	where	a.nr_sequencia = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and (a.ie_tipo_atend_tiss = 7 or coalesce(a.ie_regime_atendimento,9) = 3)
	and	a.ie_estagio in (5, 6, 10)
	and	(a.nr_seq_guia_principal IS NOT NULL AND a.nr_seq_guia_principal::text <> '')
	and	not exists (	select	1
				from	pls_exec_cirurgica_guia b
				where	b.nr_seq_guia = a.nr_sequencia)
	and	exists (	select	1
				from	pls_execucao_cirurgica c
				where	c.nr_seq_guia	= a.nr_seq_guia_principal
				and	c.ie_faturado	= 'N')
	
union

	--Requisicao sem execucao com guia principal sem execucao cirurgica
	select	'S' ie_iniciar,
		'N' ie_reabrir,
		a.nr_seq_guia_principal nr_seq_guia_principal
	from	pls_requisicao a
	where	a.nr_seq_guia_principal = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and (a.ie_tipo_atendimento = 7 or coalesce(a.ie_regime_atendimento,9) = 3)
	and	a.ie_estagio not in (1, 3, 7)
	and (select	b.ie_tipo_guia
		from	pls_guia_plano b
		where	b.nr_sequencia = a.nr_seq_guia_principal) = 1
	and	not exists (	select 1
				from	pls_execucao_req_item e
				where	e.nr_seq_requisicao = a.nr_sequencia)
	and	not exists (	select	1
				from	pls_exec_cirurgica_guia b
				where	b.nr_seq_requisicao = a.nr_sequencia)
	and	not exists (	select	1
				from	pls_execucao_cirurgica c
				where	c.nr_seq_guia = a.nr_seq_guia_principal)
	
union

	--Requisicao sem execucao com guia principal com execucao cirurgica
	select	'N' ie_iniciar,
		'S' ie_reabrir,
		a.nr_seq_guia_principal nr_seq_guia_principal
	from	pls_requisicao a
	where	a.nr_seq_guia_principal = nr_seq_guia_p
	and	a.ie_tipo_guia = '2'
	and (a.ie_tipo_atendimento = 7 or coalesce(a.ie_regime_atendimento,9) = 3)
	and	a.ie_estagio not in (1, 3, 7)
	and (select	b.ie_tipo_guia 
		from	pls_guia_plano b
		where	b.nr_sequencia = a.nr_seq_guia_principal) = 1
	and	not exists (	select 1
				from	pls_execucao_req_item e
				where	e.nr_seq_requisicao = a.nr_sequencia)
	and	not exists (	select	1
				from	pls_exec_cirurgica_guia b
				where	b.nr_seq_requisicao = a.nr_sequencia)
	and	exists (	select	1
				from	pls_execucao_cirurgica c
				where	c.nr_seq_guia	= a.nr_seq_guia_principal
				and	c.ie_faturado	= 'N');
				
BEGIN

for r_c01_w in c01 loop
	begin
	line_w.nr_seq_guia_principal	:= r_c01_w.nr_seq_guia_principal;
	line_w.ie_iniciar_exec		:= r_c01_w.ie_iniciar;
	line_w.ie_reabrir_exec		:= r_c01_w.ie_reabrir;
	RETURN NEXT line_w;
	end;
end loop;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
